import React, { useState } from 'react'
import ExpensesList from './ExpensesList';
import ExpensesFilter from './ExpensesFilter';
import ExpensesChart from './ExpensesChart';
import './Expenses.css'

function Expenses(props) {
    const items = props.items;

    const [filteredYear, setFilteredYear] = useState('2021');

    const filterChangeHanlder = selectedYear => {
        setFilteredYear(selectedYear);
    }

    const filteredExpenses = items.filter(expense => {
        return expense.date.getFullYear().toString() === filteredYear;
    });

    const date = new Date('00:00:00');
    console.log('resultDate', date);


    return (
        <div className="expenses">
            <div className="workout-chart">
                <h3>운동통계</h3>
                <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHanlder} />
                <ExpensesChart expenses={filteredExpenses}/>
            </div>

            <div className="workout-record">
                <h3>운동기록</h3>
                <ExpensesList items = {filteredExpenses} />
            </div>
        </div>
    )
}

export default Expenses
