import React, { useState } from 'react'
import ExpenseDate from './ExpenseDate';
import Card from '../UI/Card';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt } from '@fortawesome/free-solid-svg-icons'

function ExpenseItem(props) {

    const item = props.item;

    const StyledExpenseItem = styled.li`
        background: #333;
        color: #fff;
        margin-bottom: 10%;

        &:nth-child(2n){
            background: #fff;
            color: #333;
        }

        & .expense-item{
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 5% 0;
            flex-grow: 1;
        }


        & .expense-item__date{
            padding: 0 3%;
            font-size: 13px;
            font-weight: bold;
            text-align: center;
        }

        & .expense-item__detail h2{
            margin: 0;
            text-align: center;
        }

        & .expense-item__buttons{
            height: 100%;
            display: flex;
            flex-direction: column;
            justify-content: space-around;
            padding: 0 3%;
        }

        & .expense-item__buttons button{
            background: none;
            border : none;
            cursor: pointer;
        }

        & .expense-item__remove{
            color: #fff;
        }

    `;


    return (
        <StyledExpenseItem>
            <Card>
                <div className="expense-item">
                    <div className="expense-item__date">
                        <ExpenseDate date={item.date}/>
                    </div>
                    <div className="expense-item__detail">
                        <h2>{item.title}</h2>
                        <div>{item.time}</div>
                    </div>
                    <div className="expense-item__buttons">
                        <button><FontAwesomeIcon icon={faTrashAlt} className="expense-item__remove"/></button>
                        <button>수정</button>
                    </div>
                </div>
                    {/* <button onClick={clickHandler}>Change</button>            */}
            </Card>
        </StyledExpenseItem>
    )
}

export default ExpenseItem
