import React, {useState} from 'react'
import styled from 'styled-components';

function TimePicker() {

    const [clickedHour, setClickedHour] = useState();
    const [clickedMinute, setClickedMinute] = useState();

    const StyledTimePicker = styled.div`
        display: flex;
        height: 100px;
        margin: 1rem 0;

        & .TimePicker__hour{
            display: flex;
            flex-direction: column;
            overflow-y: scroll;
        }

        & .TimePicker__minute{
            display: flex;
            flex-direction: column;
            overflow-y: scroll;
        }

        & button{
            border: none;
            cursor: pointer;
            background: #fff;
            font-size: 20px;
            color: #d2d2d2;

        }

        & .clicked{
            color : #333;
            font-weight: bold;
        }
    `;

    const hourClickHandler = (hour) => {
        setClickedHour(hour);
    }

    const minuteClickHandler = (minute) =>{
        setClickedMinute(minute);
    } 
    return (
        <StyledTimePicker>
            <div className="TimePicker__hour">
                {[...Array(12)].map((v,hour) => 
                    <button 
                        onClick={() => hourClickHandler(hour)} 
                        className={clickedHour === hour ? 'clicked' : ''}>
                        {hour < 10 ? '0' + hour : hour}
                    </button>)}
            </div>

            <div className="TimePicker__minute">
                {[...Array(60)].map((v,minute) => 
                    <button
                        onClick={() => minuteClickHandler(minute)}
                        className={clickedMinute === minute ? 'clicked': ''}>
                        {minute < 10 ? '0' + minute : minute}
                        </button>)}
            </div>

        </StyledTimePicker>
    )
}

export default TimePicker
