import * as actions from '../_actions/types';

const workoutState = {
    workouts : [{
      id: '0',
      title: '산책',
      time: '00:50',
      date: new Date(2020, 7, 14),
    },
    { 
      id: '1', 
      title: '테니스',
      time: '00:50',
      date: new Date(2021, 2, 12) },
    {
      id: '2',
      title: '테니스',
      time: '01:10',
      date: new Date(2021, 2, 28),
    },
    {
      id: '3',
      title: '산책',
      time: '01:50',
      date: new Date(2021, 5, 12),
    }]
  };

const nextWorkoutId = workouts => {
    const maxId = workouts.reduce((maxId, workout) => Math.max(workout.id, maxId), -1);
    return maxId + 1;    
};

export default function workoutReducer(state = workoutState, action){
    switch (actions) {
        case actions.WORKOUT_ADDED:
          const workout = action.payload 
            return {
                ...state,
                workouts : {
                    ...state.workouts,
                    [workout.id] : nextWorkoutId(state.workouts)
                }
              }
        default:
            return state;
    }
}
